mapper.select:
SELECT t1.id, t1.account_name, t1.avatar, t1.phone, t1.account_id, t2.tenant_id FROM account_instance t1 left join account_tenant t2 on t1.id = t2.user_id WHERE 1=1
AND t1.account_name = #{paramQuery.accountName}
AND t2.tenant_id = #{paramQuery.tenantId}

SELECT t1.id, t1.account_id, t1.account_name, t1.phone, t1.store_id, t6.name as store_name, t2.name, t2.sex, t5.identifier, t2.birthday, t2.job, t3.org_name, t4.role_name, t2.entry_time
FROM account_instance t1 left join account_user_info t2 on t1.account_id = t2.account_id left join account_group t3 on t1.account_id = t3.account_id left join account_role t4 on t1.account_id = t4.principal_id left join account_identifier t5 on t1.account_id = t5.account_id
left join store_instance t6 on t1.store_id = t6.store_id
WHERE 1=1 and t1.deleted = 0
AND t1.id = #{accountInstanceQuery.id}
AND t2.name = #{accountInstanceQuery.name}
AND t2.sex = #{accountInstanceQuery.gender}
AND t3.org_id = #{accountInstanceQuery.orgId}
AND t2.job = #{accountInstanceQuery.job}
AND t1.store_id = #{accountInstanceQuery.storeId}
AND t4.role_id = #{accountInstanceQuery.roleId}
AND DATE(t2.entry_time) >= #{accountInstanceQuery.entryDateStart}
AND DATE(t2.entry_time) <= #{accountInstanceQuery.entryDateEnd}

select COUNT(1) from account_instance


         AND store_id = #{accountCountTenantQuery.storeId}


         AND DATE(dt) >= #{accountCountTenantQuery.startDate}


         AND DATE(dt) <= #{accountCountTenantQuery.endDate}

select COUNT(1) from order_instance


        AND store_id = #{accountCountTenantQuery.storeId}


        AND DATE(dt) >= #{accountCountTenantQuery.startDate}


        AND DATE(dt) <= #{accountCountTenantQuery.endDate}

    and pay_state = 1 and refund  <>  1

select COUNT(1) from market_instance


        AND store_id = #{accountCountTenantQuery.storeId}


        AND DATE(dt) >= #{accountCountTenantQuery.startDate}


        AND DATE(dt) <= #{accountCountTenantQuery.endDate}

    and market_state = 1 and market_name_id = '1623954557644095490'

select.if:
select
'0次' as count,
count(1) as preCount
from account_instance ai left join order_instance oi on oi.account_id=ai.account_id
where oi.account_id is null

    AND ai.store_id = #{accountInstanceTenantBo.storeId}


    AND DATE(ai.dt) >= #{accountInstanceTenantBo.startDate}


    AND DATE(ai.dt) <= #{accountInstanceTenantBo.endDate}

select '1-6次' as count, count(1) as preCount from (
    select account_id from order_instance


            AND store_id = #{accountInstanceTenantBo.storeId}


            AND DATE(dt) >= #{accountInstanceTenantBo.startDate}


            AND DATE(dt) <= #{accountInstanceTenantBo.endDate}

        and pay_state = 1 and refund  <>  1
        group by account_id HAVING count(account_id) > 0 and count(account_id) < 6

) tab

select '6-12次' as count, count(1) as preCount from (
select account_id from order_instance


        AND store_id = #{accountInstanceTenantBo.storeId}


        AND DATE(dt) >= #{accountInstanceTenantBo.startDate}


        AND DATE(dt) <= #{accountInstanceTenantBo.endDate}

    and pay_state = 1 and refund  <>  1
    group by account_id HAVING count(account_id) > 5 and count(account_id) < 12

) tab

select '12次以上' as count, count(1) as preCount from (
select account_id from order_instance


        AND store_id = #{accountInstanceTenantBo.storeId}


        AND DATE(dt) >= #{accountInstanceTenantBo.startDate}


        AND DATE(dt) <= #{accountInstanceTenantBo.endDate}

    and pay_state = 1 and refund  <>  1
    group by account_id HAVING count(account_id) > 12

) tab

mapper.select:
select count(1) as preCount from (
select account_id from order_instance oi left join iff_setting itt on oi.store_id = itt.store_id


        AND oi.store_id = #{accountInstanceTenantBo.storeId}


        AND DATE(oi.dt) >= #{accountInstanceTenantBo.startDate}


        AND DATE(oi.dt) <= #{accountInstanceTenantBo.endDate}

    and itt.rule_num = 100001 and itt.content is not null
    and oi.pay_state = 1 and oi.refund  <>  1

group by oi.account_id HAVING sum(oi.amount)  >   #{accountInstanceTenantBo.frequencyStartCount} and sum(oi.amount)  <  #{accountInstanceTenantBo.frequencyEndCount}
) tab

select count(1) as preCount from (
select
ai.account_id
from account_instance ai left join order_instance oi on oi.account_id=ai.account_id


        AND ai.store_id = #{accountInstanceTenantBo.storeId}


        AND DATE(ai.dt) >= #{accountInstanceTenantBo.startDate}


        AND DATE(ai.dt) <= #{accountInstanceTenantBo.endDate}

    and oi.pay_state = 1 and oi.refund  <>  1 and oi.dt >= subdate(#{accountInstanceTenantBo.endDate},#{accountInstanceTenantBo.distributionCount})
    group by ai.account_id

) tab
